  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"layout"}}>{`Layout`}</MDXTag>
<MDXTag name="p" components={components}>{`The Layout element is simply a wrapper element used to provide proper spacing to nested Layout Sections.`}</MDXTag>
<MDXTag name="p" components={components}>{`Use a Layout element whenever you need to provide horizontal padding and vertical spacing to sections of a page or to divide content into multiple columns.`}</MDXTag>
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="h1" components={components} props={{"id":"layout-section"}}>{`Layout Section`}</MDXTag>
<MDXTag name="p" components={components}>{`Layout Section creates spacing between sections of content within a layout.`}</MDXTag>
<MDXTag name="p" components={components}>{`Use a Layout Section element to divide content within a Layout into separate vertical sections or horizontal columns.
By default, each Layout Section will fill the entire width of its parent.`}</MDXTag>
<MDXTag name="p" components={components}>{`Secondary Layout Sections can be used to create multi-column layouts.`}</MDXTag>
<MDXTag name="p" components={components}>{`Combining a Primary Layout Section and a Secondary Layout Section will create a multi-column layout with a larger primary width column and smaller secondary width column.`}</MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
