  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"card"}}>{`Card`}</MDXTag>
<MDXTag name="p" components={components}>{`Cards bundle together content, and related links and actions.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"guidelines"}}>{`Guidelines:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Every card should solve a problem for the user`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Cards should communicate their own level of priority`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Card states must be persistent`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Cards should be concise and relevant to the user`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Important actions should be represented as buttons in the card footer`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Less important actions may be included in the header; a single `}<MDXTag name="a" components={components} parentName="li" props={{"href":"/components/detail/simple-button"}}>{`Simple Button`}</MDXTag>{` may be used, or a Context Menu if there are many actions`}</MDXTag>
</MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
