  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"heartwood-components"}}>{`Heartwood Components`}</MDXTag>
<MDXTag name="p" components={components}>{`Heartwood is a design system made of foundational decisions that ensure cohesion, allow for flexibility, and enable a smooth workflow.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"goals"}}>{`Goals`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`Translate static designs into live components built with HTML, CSS, and client-side JS`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Generate styles to be used by React components in `}<MDXTag name="a" components={components} parentName="li" props={{"href":"https://github.com/sprucelabsai/workspace.sprucebot-skills-kit/tree/dev/packages/react-sprucebot"}}>{`React Sprucebot`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"setup"}}>{`Setup`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`git clone`}</MDXTag>{` this repo`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`cd sprucebot-heartwood`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Install node modules: `}<MDXTag name="inlineCode" components={components} parentName="li">{`nvm use && yarn`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Install Gulp and Fractal CLI tools installed globally: `}<MDXTag name="inlineCode" components={components} parentName="li">{`yarn global @frctl/fractal && yarn global gulp-cli`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"running-the-project-locally"}}>{`Running the project locally`}</MDXTag>
<MDXTag name="ol" components={components}>
