  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"modal"}}>{`Modal`}</MDXTag>
<MDXTag name="p" components={components}>{`Modals enable users to perform complex tasks without leaving the current view.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"guidelines"}}>{`Guidelines:`}</MDXTag>
<MDXTag name="p" components={components}>{`Context; a Modal should include enough information for a user to know what they’re doing at a glance
Accessibility; any Modal should be dismissible via the Esc key, and forms in Modals should behave just like any other form.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"when-to-use"}}>{`When to use:`}</MDXTag>
<MDXTag name="p" components={components}>{`Use a Modal for tasks of medium complexity.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"when-not-to-use"}}>{`When not to use:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Simple tasks; if the task is very simple, avoid using a Modal`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`High complexity; if the task is of high complexity, it may be preferable to push the user to a different view.`}</MDXTag>
</MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
