  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"training"}}>{`Training`}</MDXTag>
<MDXTag name="p" components={components}>{`Ok, so you have a great skill! But, does anyone know how to use it? Luckily, we all share this problem and are working hard to find solutions.`}</MDXTag>
<MDXTag name="p" components={components}>{`The expected behavior for any skill is that it provides value from day one. But, teammates and owners may need a little help getting setup.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"the-will-send-training-event"}}>{`The `}<MDXTag name="inlineCode" components={components} parentName="h2">{`will-send-training`}</MDXTag>{` event`}</MDXTag>
<MDXTag name="p" components={components}>{`This `}<MDXTag name="inlineCode" components={components} parentName="p">{`event`}</MDXTag>{` is emitted whenever Sprucebot thinks it's a good time to ask the `}<MDXTag name="inlineCode" components={components} parentName="p">{`user`}</MDXTag>{` if they want to take a few minutes to setup a new Skill.`}</MDXTag>
<MDXTag name="p" components={components}>{`Here is an exampled pulled right from Vip Alerts ðŸ’¥.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`// server/events/will-send-training.js
module.exports = async (ctx, next) => {
    // only send training if this is a teammate or owner
    if (ctx.event.role === 'teammate' || ctx.event.role === 'owner') {
        // have they configured VIP alerts yet?
        const hasConfigured = await ctx.services.vip.hasConfiguredVipAlerts(
            ctx.event
        )

        // if not, lets invite them to do that
        if (!hasConfigured) {
            // construct a new and improved payload
            const payload = {
                ...ctx.event.payload,
                message: ctx.utilities.lang.getText('trainingMessage', {
                    teammate: ctx.event
                }),
                webViewQueryData: { training: 1 }
            }
            ctx.body = payload
        }
    }
    await next()
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"responding"}}>{`Responding`}</MDXTag>
<MDXTag name="p" components={components}>{`Your response to `}<MDXTag name="inlineCode" components={components} parentName="p">{`will-send-training`}</MDXTag>{` should match the `}<MDXTag name="a" components={components} parentName="p" props={{"href":"messages.md"}}><MDXTag name="inlineCode" components={components} parentName="a">{`messaging`}</MDXTag></MDXTag>{` API.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`{
    message: String, // message you want sent to the user
    linksToWebView: Bool, // defaults to true, so only set to false if needed
    webViewQueryData: Object, // passed to your skill after they click the link
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"gotchyas"}}>{`Gotchya's`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`You can never count on your message being delivered because Sprucebot may decide another skill has a more important training message to send. `}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`To combat this, always send the next training message you decide the user needs until you see they have completed some step.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`For some skills, it may be better to have them "dormant" until training is done. Vip Alerts ðŸ’¥ enables for each user as they complete training.`}</MDXTag>
</MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
