  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"meta-api"}}>{`Meta API`}</MDXTag>
<MDXTag name="p" components={components}>{`A way to store any JSON.stringify'able object and query against it.`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"for-when-you-dont-quite-need-a-database"}}>{`For when you don't quite need a database`}</MDXTag>
<MDXTag name="p" components={components}>{`There will come a time when you need to store some data about a guest, teammate, owner, location, or even your own skill.
The `}<MDXTag name="inlineCode" components={components} parentName="p">{`Meta API`}</MDXTag>{` is a basic key/value store. As such, it can not run aggregate operations (avg, sum, etc.) on the data. If you need a more advanced data store, see `}<MDXTag name="a" components={components} parentName="p" props={{"href":"models.md"}}>{`models.md`}</MDXTag>{`.`}</MDXTag>
