module.exports = {
    didEnterMessage: ({ favoriteColor, event }) =>
        favoriteColor
            ? \`Welcome back \${event.User.casualName}! Your favorite color is \${
                    favoriteColor.value
              }. That's pretty alright. \`
            : \`Hey \${event.User.casualName}, why haven't you set your favorite color?\`
}
`}</MDXTag></MDXTag>
