  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"domain-input"}}>{`Domain Input`}</MDXTag>
<MDXTag name="p" components={components}>{`A Domain Input accepts a valid subdomain under the specified domain.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"guidelines"}}>{`Guidelines:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Don’t overcorrect the user; if the value is copy-pasted and includes the parent domain, either leave it intact or remove the domain on blur. Don’t show an error message if it can be avoided.`}</MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"when-to-use"}}>{`When to use:`}</MDXTag>
<MDXTag name="p" components={components}>{`Only use a Domain Input when asking for a subdomain.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"when-not-to-use"}}>{`When not to use:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`When not asking for a subdomain; use another type of input`}</MDXTag>
</MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
