  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"language"}}>{`Language`}</MDXTag>
<MDXTag name="p" components={components}>{`At the time of this writing, we do not have full multi-lingual support. But, we've setup the foundation for when the time comes. üòÅ If you need multi-lingual support now, email `}<MDXTag name="inlineCode" components={components} parentName="p">{`scientists@sprucelabs.ai`}</MDXTag>{` and we'll get it added ASAP!`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"defining-terms"}}>{`Defining `}<MDXTag name="inlineCode" components={components} parentName="h2">{`terms`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`All your `}<MDXTag name="inlineCode" components={components} parentName="p">{`terms`}</MDXTag>{` go into `}<MDXTag name="inlineCode" components={components} parentName="p">{`interface/lang/default.js`}</MDXTag>{`. A `}<MDXTag name="inlineCode" components={components} parentName="p">{`term`}</MDXTag>{` can be a String or a Function that returns a String.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`// interface/lang/default.js
module.exports = {
    botName: 'Sprucebot',
    botNamePlural: 'Sprucebots',
    teamDashboardWelcome: ({ user, botName }) =>
        \`Welcome back \${user.User.causalName}!\`,
    teamDashboardBotText: ({ user }) =>
        user.status === 'online'
            ? \`You are at \${user.Location.name} as we speak! That's so cool! üôåüèº\`
            : \`Next time you get into \${
                    user.Location.name
              }, don't forget to join the wifi! üëäüèº\`,
    teammateDashboardHeading: \`Who's Online\`,
    errorLoadingGuests: \`Oh no! I could not load guests!\`,
    errorLoadingTeammates: \`Oh no! I could not load teammates!\`,
    guestsTabTitle: \`Guests\`,
    teammatesTabTitle: \`Teammates\`
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`There are 2 big reasons we like making `}<MDXTag name="inlineCode" components={components} parentName="p">{`terms`}</MDXTag>{` a Function.`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Placeholder support: `}<MDXTag name="inlineCode" components={components} parentName="li">{`How are you \${user.User.name}`}</MDXTag>{`!`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Simple logic: user.status === 'online' ? `}<MDXTag name="inlineCode" components={components} parentName="li">{`online message`}</MDXTag>{` : `}<MDXTag name="inlineCode" components={components} parentName="li">{`offline message`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"when-to-put-logic-in-a-term"}}>{`When to put logic in a `}<MDXTag name="inlineCode" components={components} parentName="h2">{`term`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`There are 2 reasons we like to put `}<MDXTag name="strong" components={components} parentName="p">{`simple`}</MDXTag>{` logic in a `}<MDXTag name="inlineCode" components={components} parentName="p">{`term`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Messages can change often depending on context (e.g. `}<MDXTag name="inlineCode" components={components} parentName="li">{`user.status`}</MDXTag>{`), so rather than trying to build every case into our code (with many calls to `}<MDXTag name="inlineCode" components={components} parentName="li">{`getText()`}</MDXTag>{`), we like to let the copy writers handle the variations.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Many languages are more complicated than English and rely on context for structure, so we're prepping for handling those cases.`}</MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"server-side"}}><MDXTag name="inlineCode" components={components} parentName="h2">{`Server`}</MDXTag>{` side`}</MDXTag>
<MDXTag name="p" components={components}>{`On the `}<MDXTag name="inlineCode" components={components} parentName="p">{`server`}</MDXTag>{` you can access `}<MDXTag name="inlineCode" components={components} parentName="p">{`lang`}</MDXTag>{` on the `}<MDXTag name="inlineCode" components={components} parentName="p">{`ctx`}</MDXTag>{`, as follows:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`// server/controllers/1.0/owner/index.js
module.exports = (router) => {

    router.post('/api/1.0/owner/settings.json', async (ctx, next) => {

        const message = ctx.lang.getText('aLanguageKey')

    }

}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"interface-side"}}><MDXTag name="inlineCode" components={components} parentName="h2">{`Interface`}</MDXTag>{` side`}</MDXTag>
<MDXTag name="p" components={components}>{`The same, but using `}<MDXTag name="inlineCode" components={components} parentName="p">{`this.props.lang`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`render() {

    const { auth } = this.props;

    return (
        <Page className="owner-settings" header={{
            title: this.props.lang.getText('ownerSettingsHeading', {
                owner: auth
            })
        }}>
            <PageContent><BotText>{this.props.lang.getText('ownerSettingsBotTex', {
                owner: auth
            })}</BotText></PageContent>
        </Page>
    )
}
`}</MDXTag></MDXTag>
<MDXTag name="h1" components={components} props={{"id":"whats-next"}}>{`What's next?`}</MDXTag>
<MDXTag name="p" components={components}>{`Ok, now that we know how to handle language, lets dive into `}<MDXTag name="a" components={components} parentName="p" props={{"href":"messages.md"}}><MDXTag name="inlineCode" components={components} parentName="a">{`messaging`}</MDXTag></MDXTag>{`.`}</MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
