  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"secondary-button"}}>{`Secondary Button`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"when-to-use"}}>{`When to use:`}</MDXTag>
<MDXTag name="p" components={components}>{`Use a Secondary Button for any action that is not the primary action, such as cancelling a form submission or exiting a task.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"when-not-to-use"}}>{`When not to use:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`For a destructive action; instead, use a `}<MDXTag name="a" components={components} parentName="li" props={{"href":"/components/detail/caution-button"}}>{`Caution Button`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`For the primary call to action; instead, use a `}<MDXTag name="a" components={components} parentName="li" props={{"href":"/components/detail/primary-button"}}>{`Primary Button`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`In a visually cluttered context, it may be better to use a `}<MDXTag name="a" components={components} parentName="li" props={{"href":"/components/detail/simple-button"}}>{`Simple Button`}</MDXTag>{`.`}</MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"uses-these-components"}}>{`Uses these components`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"/components/detail/loader"}}>{`Loader`}</MDXTag></MDXTag>
</MDXTag>
           </MDXTag>
  }
}
